INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Desafio do Consumo de Energia Diária', 'Monitore o consumo de energia da sua casa por um dia e identifique os maiores consumidores. Desligue ou desconecte aparelhos que estão em modo stand-by.', 'Reduzir o consumo diário em pelo menos 5%', 50);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Dia da Energia Verde', 'Passe um dia inteiro utilizando o mínimo possível de energia da rede elétrica. Aproveite ao máximo a luz natural, evite usar grandes eletrodomésticos e, se possível, utilize energia renovável, como baterias solares portáteis.', 'Reduzir o consumo energético em 90% em um único dia', 100);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Meta de Eficiência Energética Semanal', 'Durante uma semana, registre seu consumo de energia e busque reduzir em comparação à semana anterior. Utilize práticas como reduzir o uso de aparelhos em stand-by e otimizar o uso de iluminação.', 'Reduzir o consumo semanal em 10%', 75);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Desafio do Apagar Luzes', 'Durante três dias, desenvolva o hábito de apagar as luzes ao sair de um cômodo. O aplicativo vai monitorar ou permitir que você registre o número de vezes que se lembra de apagar as luzes.', 'Apagar as luzes ao sair de um cômodo por no mínimo 10 vezes', 30);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Hora do Desligamento', 'Todos os dias, ao final do expediente (ou à noite), desligue e desconecte todos os aparelhos eletrônicos desnecessários, como computadores, televisores e carregadores.', 'Implementar a prática de desligar dispositivos ao longo de 7 dias consecutivos', 40);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Reduza o Uso do Ar-Condicionado', 'Evite o uso do ar-condicionado por uma semana. Substitua-o por ventiladores, janelas abertas ou simplesmente ajustando o termostato para um nível mais eficiente.', 'Reduzir o uso do ar-condicionado em 50% durante uma semana', 80);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Desligue Stand-by', 'Identifique todos os aparelhos que ficam em modo stand-by na sua casa (como televisores, micro-ondas, videogames) e desconecte-os da tomada sempre que não estiverem em uso.', 'Manter os aparelhos fora do stand-by por 7 dias', 60);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Tecnologia Dormindo', 'Programe seus aparelhos para entrarem em modo econômico ou desligar automaticamente quando não estiverem sendo usados (televisores, computadores, etc.).', 'Configurar ao menos 3 dispositivos em modo de economia de energia', 40);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Banho Consciente', 'Reduza o tempo do seu banho e economize energia elétrica. Substitua os banhos de 10 minutos por banhos de 5 minutos.', 'Manter os banhos de no máximo 5 minutos por uma semana', 50);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Lavagem Inteligente', 'Use sua máquina de lavar roupa ou louça somente quando estiver com a carga máxima. Isso economiza energia e água.', 'Usar a máquina de lavar em carga máxima por 3 ciclos consecutivos', 30);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Desafio Solar', 'Se você tem acesso a energia solar, desafie-se a alimentar parte da sua casa ou dispositivos usando essa energia por um dia. Caso não tenha, utilize dispositivos que possam ser carregados com baterias solares.', 'Utilizar ao menos 10% da energia de fontes renováveis por um dia', 100);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Cozinhar de Forma Sustentável', 'Utilize menos energia elétrica ao cozinhar, optando por panelas de pressão, fornos com bom isolamento térmico ou preparando refeições que não exijam muito uso de energia, como saladas e comidas cruas.', 'Reduzir o uso de eletrodomésticos na cozinha em 30% por 5 dias', 50);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Economia no Transporte', 'Diminua o uso do carro particular por uma semana e utilize transporte público, caronas ou bicicletas.', 'Reduzir o uso do carro particular em 70% por uma semana', 100);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Recarga Inteligente', 'Evite deixar seus dispositivos eletrônicos, como celulares e laptops, carregando após atingirem 100% da bateria. Isso economiza energia e aumenta a vida útil dos aparelhos.', 'Manter os dispositivos fora da tomada ao atingir 100% de carga por 5 dias', 40);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Troca por LED', 'Substitua as lâmpadas incandescentes ou fluorescentes da sua casa por lâmpadas de LED, que consomem menos energia e duram mais.', 'Substituir ao menos 5 lâmpadas por LEDs', 80);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Férias Ecológicas', 'Ao sair de casa por um longo período (como durante férias ou viagens), desligue todos os eletrodomésticos e desconecte da tomada.', 'Manter os aparelhos desconectados durante toda a viagem', 100);
INSERT INTO watteco_missao (titulo, descricao, meta, pontos) VALUES
    ('Monitoramento Constante', 'Utilize sensores ou registre manualmente seu consumo de energia todos os dias por uma semana e veja como pequenas mudanças afetam o total de energia usada.', 'Monitorar o consumo por 7 dias consecutivos', 50);
